actionsTODO.map:
function(doc) {
if (doc.class == "Agenda" && (doc.Status=="TODO" || doc.Status=="ON")) {
 if(doc.societe.id)
  emit(doc.societe.id, doc);
 if(doc.contact.id)
  emit(doc.contact.id, doc);
}
}

actionsDONE.map:
function(doc) {
if (doc.class == "Agenda" && doc.Status=="DONE") {
 if(doc.societe.id)
  emit(doc.societe.id, doc);
 if(doc.contact.id)
  emit(doc.contact.id, doc);
}
}

listMyTasks.map:
function(doc) {
if (doc.class == "Agenda" && doc.Status!='DONE') {
if(doc.usertodo.id)
  emit([doc.usertodo.id,doc.datep], doc);
}
}

listMyDelegatedTasks.map:
function(doc) {
if (doc.class == "Agenda" && doc.Status!='DONE') {
if(doc.author.id != doc.usertodo.id)
  emit([doc.author.id,doc.datep], doc);
}
}

listTODOByUser.map:
function(doc) {
if (doc.class == "Agenda" && doc.Status!='DONE') {
if(doc.usertodo.id)
  emit([doc.usertodo.id,doc.datep], doc);
else if(doc.author.id)
  emit([doc.author.id,doc.datep], doc);
}
}

listDONEByUser.map:
function(doc) {
if (doc.class == "Agenda" && doc.Status =='DONE') {
if(doc.usertodo.id)
  emit([doc.usertodo.id,doc.datep], doc);
else if(doc.author.id)
  emit([doc.author.id,doc.datep], doc);
else if(doc.userdone.id)
  emit([doc.userdone.id,doc.datep], doc);
}
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
