listUnpaid.map:
function(doc) {
if (doc.class && doc.class=="Facture" && (!doc.paye || doc.paye != doc.total_ttc))
  emit(doc.socid, doc);
}

paymentsPerFacture.map:
function(doc) {
if (doc.class && doc.class == "Paiement") {
for (i in doc.amounts) {
  emit(i, {id: doc._id, datepaye: doc.datepaye, amount: parseFloat(doc.amounts[i])});
}
}
}

sumPaymentsPerFacture.map:
function(doc) {
if (doc.class && doc.class == "Paiement") {
for (i in doc.amounts) {
  emit(i, parseFloat(doc.amounts[i]));
}
}
}

listLinkedObjects.map:
function(doc) {
if (doc.class && (doc.class == "Propal" || doc.class == "Commande" || doc.class == "Facture")) {
for (i in doc.linked_objects)
emit(doc.linked_objects[i].id, doc);
}
}

listByDate.map:
function(doc) {
if (doc.class && doc.class == "Facture" && doc.type == "INVOICE_STANDARD"){
emit(doc.date, doc);
}
}

amountByDate.map:
function(doc) {
if (doc.class && doc.class == "Facture" && doc.type == "INVOICE_STANDARD"){
emit(doc.date, doc.total_ttc);
}
}

averageByDate.map:
function(doc) {
if (doc.class && doc.class == "Facture" && doc.type == "INVOICE_STANDARD"){
emit(doc.date, doc.total_ttc);
}
}

listNotPaidPerSociete.map:
function(doc) {
if (doc.class && doc.class == "Facture" && doc.Status == "NOT_PAID")
  emit(doc.socid, doc);
}

listReplacedInvoices.map:
function(doc) {
if (doc.class && doc.class == "Facture" && doc.fk_facture_source)
  emit(doc.fk_facture_source, doc._id);
}

listAvoirableInvoices.map:
function(doc) {
if (doc.class && doc.class == "Facture" && (doc.Status == "NOT_PAID" || doc.Status == "STARTED" || doc.Status == "PAID" || doc.Status == "PAID_PARTIALLY") && (doc.type != "INVOICE_REPLACEMENT" && doc.type != "INVOICE_AVOIR")) 
  emit(doc._id, doc);
}

listAvoirableInvoicesPerSociete.map:
function(doc) {
if (doc.class && doc.class == "Facture" && (doc.Status == "NOT_PAID" || doc.Status == "STARTED" || doc.Status == "PAID" || doc.Status == "PAID_PARTIALLY") && (doc.type != "INVOICE_REPLACEMENT" && doc.type != "INVOICE_AVOIR")) 
  emit(doc.socid, doc);
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
