list.map:
function(doc) {
  if(doc.class && doc.class=="Societe" && doc._id != "societe:mysoc" && doc.trash != true)
  emit(doc.name, doc);
}

target_id.map:
function(doc) {
  if(doc.class && doc.class=="Societe" && doc._id != "societe:mysoc" && doc.trash != true)
  	emit(doc._id, {_id:doc._id, _rev:doc._rev});
}

count_status.map:
function(doc) {
  if(doc.class && doc.class=="Societe" && doc._id != "societe:mysoc" && doc.trash != true)
  emit(doc.Status, 1);
}

list_customer_id.map:
function(doc) {
  if(doc.class && doc.code_client && doc.class=="Societe" && doc.trash != true)
  emit(doc.code_client, doc._id);
}

tag.map:
function(doc) {
if(doc.class=="Societe" && doc.Tag.length > 0) {
    for(var idx in doc.Tag) {
      emit(doc.Tag[idx], 1);
    }
  }
}

commercial_status.map:
function(doc) {
  if(doc.class && doc.class=="Societe" && doc.commercial_id.id && doc._id != "societe:mysoc" && doc.trash != true)
  emit([doc.commercial_id.name,doc.Status], 1);
}

listByCommercial.map:
function(doc) {
  if(doc.class && doc.class=="Societe" && doc.commercial_id.id && doc._id != "societe:mysoc" && doc.trash != true)
  emit(doc.commercial_id.id, doc);
}

listZoneGeo.map:
function(doc) {
  if(doc.class && doc.class=="Societe" && doc._id != "societe:mysoc"  && doc.trash != true && doc.zonegeo)
  emit(doc.zonegeo, doc);
}

listGPS.map:
function(doc) {
  if(doc.class && doc.class=="Societe" && doc._id != "societe:mysoc" && doc.trash != true && doc.gps && doc.Status!="ST_NEVER" && doc.Status!="ST_NO")
  emit(doc.name, doc);
}

updates.in-place:
function (doc, req) {
 var field = req.query.field;
 var value = req.query.value;
 doc[field] = value;
  return [doc, value];
 }
